  tasks.map(task => {
    return (
      <div key={task.id} className={task.reminder ? "task reminder" : "task"}>
        <div>
          <h3>{task.text}</h3>
          <span>{task.day}</span>
        </div>
        <div className="close">cc</div>
      </div>
    )
  })


  return (
    <>
      
    </>
  )
}

export default Tasks
