const Header = ({showAdd, onShowAdd}) => {
  return (
    <header className="header">
      <h1>To Do List</h1>
      <button style={{color: showAdd && "green"}} className="btn" onClick={onShowAdd}>{showAdd ? "close" : "add"}</button>
    </header>
  )
}

export default Header
