const Tasks = (tasks) => {
  return (
    <>
      {
        tasks.map(task => {
          return (
            <div className={tasks.reminder ? "task reminder" : "task"}>
              <div>
                <h3>{tasks.text}</h3>
                <span>{tasks.day}</span>
              </div>
              <div className="close">cc</div>
            </div>
          )
        })
      }
    </>
  )
}

export default Tasks
