const Task = ({ task }) => {
  console.log(task)
  return (
    <div key={pops.task.id} className={pops.reminder ? "task reminder" : "task"}>
      <div className="task-text">
        <h3>{pops.text}</h3>
        <p>{pops.day}</p>
      </div>
      <i className="fa-solid fa-xmark close" onClick={pops.isDeleting}></i>
    </div>
  )
}

export default Task
