const Task = ({task}) => {
  return (
    <div key={task.id} className={task.reminder ? "task reminder" : "task"}>
      <h3>{task.Tasktext}       <i className="fa-solid fa-xmark"></i>
</h3>
      <p>{task.day}</p>
    </div>
  )
}

export default Task
