import { useState } from "react"

const AddTask = () => {

  let [formData, setFormData] = useState({

  })

  function updateForm(event, target) {
    console.log(event)
  }

  function handelSubmit(event) {
    event.preventDefault()
  }

  return (
    <form className='add-form' onSubmit={handelSubmit}>
      <div className='form-control'>
        <label>Task</label>
        <input type="text" placeholder="Add Task" onChange={() => updateForm(event.target, "text")} />
      </div>
      <div className='form-control'>
        <label>Day & Time</label>
        <input type="text" placeholder="Add Day & Time" onChange={() => updateForm(event, "day")} />
      </div>
      <div className='form-control form-control-check'>
        <label>Set Reminder</label>
        <input type="checkbox" onChange={updateForm} onChange={() => updateForm(event, "reminder")} />
      </div>
      <button type="submit" className='btn btn-block'>Save Task</button>
    </form>
  )
}
export default AddTask
