import { useState } from "react"
import { nanoid } from 'nanoid'

const AddTask = (pops) => {
  
  let [formData, setFormData] = useState({
    id: nanoid(),
    text: '',
    day: '',
    reminder: false,
  })

  function updateForm(target, name) {
    const {value, checked, type} = target
    console.log(formData)
    setFormData({
      ...formData,
      [name]: type === "checkbox" ? checked : value
    })
  }

  function handelSubmit(e) {
    e.preventDefault()
    setFormData({
      ...formData,
      text: '',
      day: '',
      reminder: false,
    })
  }

  return (
    <form className='add-form' onSubmit={handelSubmit}>
      <div className='form-control'>
        <label>Task</label>
        <input type="text" placeholder="Add Task" value={formData.text} onChange={e => updateForm(e.target, "text")} />
      </div>
      <div className='form-control'>
        <label>Day & Time</label>
        <input type="text" placeholder="Add Day & Time" value={formData.day} onChange={e => updateForm(e, "day")} />
      </div>
      <div className='form-control form-control-check'>
        <label>Set Reminder</label>
        <input type="checkbox" value={formData.reminder} onChange={e => updateForm(e, "reminder")} />
      </div>
      <button type="submit" className='btn btn-block' onClick={() => pops.addNewTask(formData)}>Save Task</button>
    </form>
  )
}

export default AddTask
