const Task = ({task}) => {
  return (
    <div key={task.id} className={task.reminder ? "task reminder" : "task"}>
      <div>
        <h3>{task.Tasktext}</h3>
        <p>{task.day}</p>
      </div>
      <i class="fa-brands fa-facebook"></i>
    </div>
  )
}

export default Task
