import { useState, useEffect } from 'react'
import Header from './components/Header';
import AddTask from './components/AddTask';
import Tasks from './components/Tasks';
import './App.css';

function App() {

  const [tasks, setTasks] = useState([])

  function addNewTask(newTask) {
    setTasks([...tasks, newTask])
  }

  function isDeleting(deletedTaskId) {
    setTasks(
      tasks.filter(task => task.id !== deletedTaskId)
    )
  }

  function change(changeTask) {
    console.log(changeTask)
    setTasks(tasks.map(task => task.id === changeTask ? {...task, reminder: !task.reminder} : task))
  }

  return (
    <div className="container">
      <Header />
      <AddTask addNewTask={addNewTask} />
      <Tasks tasks={tasks} gg={"basel"} onDelete={deleteTask} change={change} />
    </div>
  );
}

export default App;
